func stringToArr5(_ str: String) -> [Character] {
    var arr = [Character]()
    for c in str{
        arr.append(c)
    }
    return arr
}

//  Definition for singly-linked list.
public class ListNode {
    public var val: Int
    public var next: ListNode?
    public init(_ val: Int) {
        self.val = val
        self.next = nil
    }
}

