func threeSum(_ nums: [Int]) -> [[Int]] {
    var result = [[Int]]()
    let arr = nums.sorted(by: <)
    var i = 0,j = 0,k = 0
    while i < arr.count - 1 {
        if arr[i] > 0{
            break
        }else{
            if i>0 && arr[i] == arr[i-1]{
                 i += 1
                continue
            }
        }
        let a = arr[i]
        j = i+1
        k = arr.count-1
        
        while j<k {
            let b = arr[j]
            let c = arr[k]
            if a+b+c>0{
                k -= 1
            }else if a+b+c<0{
                j += 1
            }else{
                result.append([a,b,c])
                while j<k && arr[j] == arr[j+1]{
                    j += 1
                }
                while j<k && arr[k] == arr[k-1]{
                    k -= 1
                }
                j += 1
                k -= 1
            }
        }
        i += 1
    }
    return result
}

