func removeNthFromEnd(_ head: ListNode?, _ n: Int) -> ListNode? {
    guard head != nil else{
        return nil
    }
    var arr = [head!.val]
    var next = head!.next
    while next != nil {
        arr.append(next!.val)
        next = next!.next
    }
    arr.remove(at: arr.count - n)
    var result:ListNode?
    var i = arr.count - 1
    while i>=0 {
        let v = arr[i]
        let ln = ListNode(v)
        ln.next = result
        result = ln
        i -= 1
    }
    return result
}

func threeSumClosest(_ nums: [Int], _ target: Int) -> Int {
    if nums.count == 0{
        return 0
    }
    let arr = nums.sorted(by: <)
    var result = 0
    var min = max(abs(arr.last! * 3 - target), abs(arr.first! * 3 - target))
    var i = 0,j = 0,k = 0
    while i < arr.count-2{
        let a = arr[i]
        j = i+1
        k = arr.count-1
        while j<k {
            let b = arr[j]
            let c = arr[k]
            if abs((a+b+c)-target)<=min{
                result = a+b+c
                min = abs((a+b+c)-target)
                print(a,b,c)
            }
            if a+b+c > target{
                k -= 1
            }else if a+b+c < target {
                j += 1
            }else{
                return a+b+c
            }
        }
        i += 1
    }
    return result
}
