func removeDuplicates(_ nums: inout [Int]) -> Int {
    var a = nums.first
    var i = 1
    while i < nums.count {
        if nums[i] == a{
            nums.remove(at: i)
        }else{
            a = nums[i]
            i += 1
        }
    }
    return nums.count
}

