func strStr(_ haystack: String, _ needle: String) -> Int {
    if needle == ""{
        return 0
    }
    let arr1 = stringToArr5(haystack)
    let arr2 = stringToArr5(needle)
    if arr1.count < arr2.count{
        return -1
    }
    var i = 0,j = 0,last = 0,result = -1
    while i < arr1.count,j<arr2.count {
        if arr1[i] == arr2[j]{
            if j == 0{
                last = i
            }
            i += 1
            j += 1
            if j == arr2.count{
                result = last
            }
        }else{
            last += 1
            i = last
            j = 0
            result = -1
        }
    }
    return result
}

