func lengthOfLastWord(_ s: String) -> Int {
    let arr = s.components(separatedBy: " ").filter({$0 != ""})
    return arr.last?.count ?? 0
}

lengthOfLastWord("b a  ")

func plusOne(_ digits: [Int]) -> [Int] {
    var i = digits.count - 1
    var arr = [Int]()
    var carr = 1
    while i > -1{
        var t = digits[i]
        t += carr
        arr.insert(t == 10 ? 0 : t, at: 0)
        carr = t == 10 ? 1 : 0
        i -= 1
    }
    if carr == 1{
        arr.insert(1, at: 0)
    }
    return arr
}

func addBinary(_ a: String, _ b: String) -> String {
    
    let dic:[Character:Int] = ["1":1,"0":0]
    let a1 = a.compactMap({dic[$0]})
    let a2 = b.compactMap({dic[$0]})
    var i = a1.count - 1, j = a2.count - 1
    var carr = 0
    var arr = [Int]()
    while i > -1 || j > -1 {
        var r = carr
        if i > -1{
            r += a1[i]
        }
        if j > -1{
            r += a2[j]
        }
        if r > 1{
            carr = 1
            r = r == 2 ? 0 : 1
        }else{
            carr = 0
        }
        arr.insert(r, at: 0)
        i -= 1
        j -= 1
    }

    if carr == 1{
        arr.insert(1, at: 0)
    }
    var str = ""
    for c in arr{
        str += String(c)
    }
    return str
}



